        </DetailDiv>
      </CoverAgreeDiv>

      <CenterDiv>
        <NextButton
          onClick={() => {
            if (termsOfUseCheck && personalCheck && gradingCheck && examineeCheck) {
              window.location.href = 'join';
            } else {
              alert('필수 항목을 모두 선택해주세요.');
            }
          }}
        >
          다음
        </NextButton>
      </CenterDiv>
    </Main>
  );
};

export default Agree;
