import React from 'react';
import styled from 'styled-components';

// 공통 스타일
const Main = styled.main`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 7rem;
  width: 100%;

  input {
    background: none;
    border: none;
    outline: none;
  }

  p {
    margin: 0;
    font-size: 1.2rem;
  }

  input::placeholder {
    color: #A1A1A1;
  } 

  button {
    border: none;
    padding: 0;
    color: black;
  }
`;

const DefaultDiv = styled.div`
  margin: 1rem 0;
  width: 80%;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 4rem;

  @media screen and (max-width: 600px) {
    margin-top: 0;
    margin-bottom: 1rem;
    height: 3rem;
  }
`;

const SelectDiv = styled(DefaultDiv)`
  margin-bottom: 3rem;

  @media screen and (max-width: 600px) {
    margin-bottom: 2rem;
  }
`;

const SelectButton = styled.button`
  background-color: #f0f0f0;
  border-radius: 10px;
  width: 50%;
  height: 4rem;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;

  &:hover {
    background-color: #f8cb37;
  }

  @media screen and (max-width: 600px) {
    width: 40vw;
    height: 12vw;
  }
`;

const GeneralButton = styled(SelectButton)`
  margin-right: 0.5rem;
`;

const CounselorButton = styled(SelectButton)`
  margin-left: 0.5rem;
`;

const InputDiv = styled(DefaultDiv)`
  background-color: #f0f0f0;
  border-radius: 10px;
  box-sizing: border-box;
  display: flex;
  align-items: center;

  @media screen and (max-width: 600px) {
    margin-top: 1rem;
    margin-bottom: 1rem;
  }
`;

const InputDefault = styled.input`
  font-size: 1.2rem;
  padding-left: 1.5rem;
  width: 100%;

  &::placeholder {
    color: #a1a1a1;
  }

  @media screen and (max-width: 600px) {
    font-size: 1rem;
  }
`;

const LoginDiv = styled(DefaultDiv)`
  margin-top: 3rem;

  @media screen and (max-width: 600px) {
    margin-top: 2rem;
  }
`;

const LoginButton = styled.button`
  background-color: #ffd651;
  border-radius: 10px;
  width: 100%;
  height: 4rem;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;

  &:hover {
    background-color: #f8cb37;
  }

  @media screen and (max-width: 600px) {
    width: 80vw;
    height: 12vw;
  }
`;

const TextHover = styled.button`
  font-size: 0.8rem;
  color: black;
  background: none;
  border: none;
  cursor: pointer;

  &:hover {
    color: rgb(105, 104, 104);
  }
`;

const SnsBar = styled(DefaultDiv)`
  display: flex;
  align-items: center;
`;

const LineImg1 = styled.img`
  padding: 0 0.5rem;
  height: 0.5rem;
  width: 0.1rem;
`;

const LineImg2 = styled.img`
  width: 8rem;

  @media screen and (max-width: 600px) {
    width: 6rem;
  }
`;

const SnsBarText = styled.span`
  margin: 0.5rem;
  font-size: 0.6rem;
`;

const NaverButton = styled.button`
  cursor: pointer;
  background: none;
  border: none;

  svg rect {
    filter: brightness(1);
  }

  &:hover svg rect {
    filter: brightness(0.8);
  }
`;

export const Login = () => {
  return (
    <Main>
      <DefaultDiv>
        <p className="bold">회원 유형을 선택하세요</p>
      </DefaultDiv>
      <SelectDiv>
        <GeneralButton>
          <p>일반회원</p>
        </GeneralButton>
        <CounselorButton>
          <p>상담사</p>
        </CounselorButton>
      </SelectDiv>
      <InputDiv>
        <InputDefault type="text" placeholder="아이디를 입력하세요" />
      </InputDiv>
      <InputDiv>
        <InputDefault type="password" placeholder="비밀번호를 입력하세요" />
      </InputDiv>
      <LoginDiv>
        <LoginButton>
          <p>로그인</p>
        </LoginButton>
      </LoginDiv>
      <DefaultDiv>
        <TextHover>아이디 찾기</TextHover>
        <LineImg1 src="../svg/Line 3.svg" alt="line3" />
        <TextHover>비밀번호 찾기</TextHover>
        <LineImg1 src="../svg/Line 3.svg" alt="line3" />
        <TextHover onClick={() => window.location.href = 'agree'}>회원가입</TextHover>
      </DefaultDiv>
      <SnsBar>
        <LineImg2 src="../svg/Line 1.svg" alt="line1" />
        <SnsBarText>sns 계정으로 로그인</SnsBarText>
        <LineImg2 src="../svg/Line 1.svg" alt="line1" />
      </SnsBar>
      <NaverButton>
        <svg width="50" height="50" viewBox="0 0 50 50" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect width="50" height="50" fill="url(#pattern0_364_497)" />
            <defs>
            <pattern id="pattern0_364_497" patternContentUnits="objectBoundingBox" width="1" height="1">
                <use href="#image0_364_497" transform="scale(0.00333333)" />
            </pattern>
            <image id="image0_364_497" width="300" height="300" href="data:image/png;base64,"/>
            </defs>
        </svg>
      </NaverButton>
    </Main>
  );
};

export default Login;
