import React from 'react';
import styled from 'styled-components';

const SearchBarContainer = styled.div`
    width: 80%;
    max-width: calc(600px - 30px);
    height: 35px;
    display: flex;
    padding: 0 10px;
    align-items: center;
    border: 1px solid #666;
    border-radius: 20px;
    margin: 0 auto;
`;

const SearchIcon = styled.img`
    width: 20px;
    height: 20px;
    margin-right: 5px;
`;

const SearchInput = styled.input`
    flex: 1;
    resize: none;
    outline: none;
    border: none;
    overflow: hidden;
    spellCheck: false;

    &::placeholder {
        color: gray;
    }
`;

const HS_SearchBar = () => {
    return (
        <SearchBarContainer>
            <SearchIcon src={`${process.env.PUBLIC_URL}/HS_images/search-icon.svg`} alt="search-icon" />
            <SearchInput name="search-location" id="search-location" placeholder="인근 역 검색" />
        </SearchBarContainer>
    );
};

export default HS_SearchBar;
